$(document).ready(function(){$.each($('img.gravatar'),function(){if(this.naturalWidth===0||this.naturalHeight===0||this.complete===false){$(this).attr('src','/images/anon-gravatar.png');}});setTimeout(function(){$('[rel="popover"]').popover();},500);setSideTab=function(){var st=$('.side_tab');var cnt=$('.content');st.css({'left':cnt.position().left+cnt.width()+st.width()});};setTimeout(setSideTab,500);$(window).on('resize',function(){setSideTab();});});